css:
.table-head{
    margin-left:1mm;
    font-size:10px;
    padding:1px;
    border:2px solid black;
    column-gap:2px solid black;
    columns:2px solid black;
    column-span:2px;
}
.table-data{
    margin-left:1mm;
    font-size:9px;
    padding:1px;
    border:2px solid black;
    column-gap:2px solid black;
    columns:2px solid black;
}
.line{
    border:2px solid black;
}
.a{
    border:1px solid black;
}
.b{
    border:1px solid black;
}

